export type Screen =
  | 'engine-select'
  | 'session-select'
  | 'connecting'
  | 'model-select'
  | 'chat';

export interface EngineConfig {
  executable: string;
  args: string[];
  model: string;
}

export const ENGINE_LABELS: Record<string, string> = {
  'claude-code': 'Claude Code',
  codex: 'Codex',
