import { spawn } from 'node:child_process';
import { ENGINES } from './types.ts';

export interface EngineAvailability {
  engine: string;
  executable: string;
  available: boolean;
}

function checkExecutable(executable: string): Promise<boolean> {
  return new Promise((resolve) => {
    const proc = spawn('which', [executable], {
      stdio: ['ignore', 'pipe', 'ignore'],
    });

    proc.on('close', (code) => {
      resolve(code === 0);
    });

    proc.on('error', () => {
      resolve(false);
    });
  });
}

export async function checkAvailableEngines(): Promise<EngineAvailability[]> {
  const results = await Promise.all(
    Object.entries(ENGINES).map(async ([engine, config]) => {
      const available = await checkExecutable(config.executable);
      return {
        engine,
        executable: config.executable,
        available,
      };
    }),
  );

  return results;
}

export async function getAvailableEngines(): Promise<string[]> {
  const results = await checkAvailableEngines();
  return results.filter((r) => r.available).map((r) => r.engine);
}

